def get_prerequisite_text(course_code:str) -> str:
    """Returns course prerequisites"""
    return get_course_info(html_content, course_code)['Prerequisites']


def get_course_title(course_code: str) -> str:
    """Returns course title"""
    return get_course_info(html_content, course_code)['Title']


def get_course_info(html_content: str, course_code:str) -> dict:
    """
    Returns course info as dict with info type as key (Code, Title, 
    Prerequisites). All values are str.

    :param html_content: plaintext html file
    :param course_code: the course code

    Preconditions: course_code is a valid course code. 
    Otherwise, returns blank dict.
    """
    soup = BeautifulSoup(html_content, 'html.parser')
    
    # Find all course divs
    course_divs = soup.find_all('div', class_='no-break no-break-courses w3-row views-row')
    
    for course_div in course_divs:
        # Extract the course title
        title = course_div.find('h3').text.strip()
        
        # Check if the course code matches
        if course_code in title:
            # Find the prerequisites section
            prerequisites = course_div.find('strong', string='Prerequisites: ')
            if prerequisites:
                prerequisites = prerequisites.find_next('span').text.strip()
                return {'Code': course_code, 
                        'Title': title.split(" • ")[1], 
                        'Prerequisites': prerequisites}
            else:
                return {'Code': "", 
                        'Title': "", 
                        'Prerequisites': ""}
    
    return {'Code': "", 'Title': "", 'Prerequisites': ""}


